        /// 根據 id 刪除 Brand
        /// </summary>
        /// <param name="id"></param>
        /// <returns></returns>
        public void Delete(List<Guid> id)
        {
            using (var transaction = _brandRepository.dbContext.Database.BeginTransaction())
            {
                try
