        /// <summary>
        /// 分頁
        /// </summary>
        /// <param name="searchModel"></param>
        /// <returns></returns>
        public Paging<BrandListResult> Paging(BrandSearchModel searchModel)
        {
            // 預設集合
            var temp = _brandRepository.GetAll();

            // 將 DB 資料轉換為列表頁呈現資料
            var tempResult = from x in temp
                             select new BrandListResult
                             {
                                 SequenceNo = x.SequenceNo,
                                 ID = x.ID,
                                 BrandCode = x.BrandCode,
                                 BrandName = x.BrandName,
                                 BrandID = x.BrandID,
                                 UpdateUser = x.UpdateUser,
                                 UpdateTime = x.UpdateTime,
                             };

            // 如有篩選條件，進行篩選
            if (!string.IsNullOrWhiteSpace(searchModel.Search))
            {
                var search = searchModel.Search.ToLower();
                tempResult = tempResult.Where(x =>
                    x.BrandCode.Contains(search) ||
                    x.BrandName.Contains(search) ||
                    x.BrandID.Contains(search) ||
                    x.UpdateUser.Contains(search) ||
                    false);
            }

            // 進行分頁處理
            var result = new Paging<BrandListResult>();
            result.total = tempResult.Count();
            result.rows = tempResult
                .OrderBy(searchModel.Sort, searchModel.Order)
                //.Skip(searchModel.Offset)
                //.Take(searchModel.Limit)
                .ToList();

            return result;
        }

