        public void Update(BrandViewModel entity)
        {
            using (var transaction = _brandRepository.dbContext.Database.BeginTransaction())
            {
                try
                {
                    var source = _brandRepository.GetByID(entity.ID);
                    source.BrandCode = entity.BrandCode ?? string.Empty;
                    source.BrandName = entity.BrandName ?? string.Empty;
                    source.BrandID = entity.BrandID ?? string.Empty;
                    source.UpdateUser = entity.UpdateUser ?? string.Empty;
                    source.UpdateTime = entity.UpdateTime;

                    _brandRepository.Update(source);
                    transaction.Commit();
                }
                catch
                {
                    transaction.Rollback();
                    throw;
                }
            }
        }
